    private static void test(int[][] grid, int expected) {
        int actual = shortestPathBinaryMatrix(grid);
        Assert.assertEquals(expected, actual);
    }

    public static int shortestPathBinaryMatrix(int[][] grid) {
        int n = grid.length;
        int n1 = n - 1;
        if (grid[0][0] == 1 || grid[n1][n1] == 1) {
            return -1;
        }
        if (n == 1) {
            return 1;
        }
        grid[0][0] = 1;
        int length = 1;
        int size = 2;
        int[] points1 = new int[2];
        while (size > 0) {
            int[] points2 = new int[size * 4];
            int size2 = 0;
            length++;
            for (int k = 0; k < size; ) {
                int i = points1[k++];
                int j = points1[k++];
                int iMin = Math.max(i - 1, 0);
                int iMax = Math.min(i + 1, n1);
                int jMin = Math.max(j - 1, 0);
                int jMax = Math.min(j + 1, n1);
                for (int i1 = iMin; i1 <= iMax; i1++) {
                    for (int j1 = jMin; j1 <= jMax; j1++) {
                        if (grid[i1][j1] == 0) {
                            if (i1 == n1 && j1 == n1) {
                                return length;
                            }
                            grid[i1][j1] = length;
                            points2[size2++] = i1;
                            points2[size2++] = j1;
                        }
                    }
                }
            }
            points1 = points2;
            size = size2;
        }
        return -1;
    }
